import React from 'react';
import { useNavigate } from 'react-router-dom';
const logo = "https://res.cloudinary.com/dzihypwia/image/upload/v1737871766/W3B_LOGO_r3v890.png";

export function Footer() {
    const navigate = useNavigate();

    const scrollToSection = (sectionId) => {
        const element = document.getElementById(sectionId);
        if (element) {
            element.scrollIntoView({ behavior: 'smooth' });
        }
    };

    const handleSubscribe = (e) => {
        e.preventDefault();
        console.log('Subscribed!');
    };

    return (
        <div className="bg-black pt-11">
            <div className="flex flex-wrap justify-between pb-16 px-6 md:px-16">
                <div className="mt-10 text-white w-full md:w-auto">
                    <div className="flex items-center mb-6">
                        <img 
                            src={logo} 
                            alt="W3B Logo" 
                            className="h-12 w-12"
                        />
                        <div className="ml-3">
                            <h1 className="text-2xl md:text-4xl text-purple-500 font-bold">W3B</h1>
                        </div>
                    </div>
                    <div className="flex flex-col md:flex-row md:w-[24rem] border-2 border-gray-900 h-auto md:h-14 rounded-lg">
                        <input 
                            type="text" 
                            placeholder="Enter your Email..." 
                            className="bg-black h-10 pl-2 py-1 focus:outline-none text-sm w-full md:w-64 mb-2 md:mb-0"
                        />
                        <button 
                            onClick={handleSubscribe}
                            className="w-full md:w-auto px-2 py-2 mr-1 bg-purple-700 text-white rounded-lg border-2 border-gray-950 hover:bg-purple-600 transition-all duration-300"
                        >
                            Subscribe Us
                        </button>
                    </div>
                </div>
                <div className="flex flex-wrap text-white w-full md:w-auto mt-10 md:mt-0">
                    <div className="mr-10 md:mr-24">
                        <div className="text-lg font-medium">Pages</div>
                        {['Home', 'About', 'Events', 'Team', 'Contact'].map((item, index) => (
                            <div 
                                key={index}
                                className="text-gray-400 pt-3 hover:text-gray-300 hover:underline cursor-pointer"
                                onClick={() => {
                                    if (item === 'Home') {
                                        navigate('/');
                                        window.scrollTo(0, 0);
                                    } else {
                                        scrollToSection(item.toLowerCase());
                                    }
                                }}
                            >
                                {item}
                            </div>
                        ))}
                    </div>
                    <div className="mr-10 md:mr-28">
                        <div className="text-lg font-medium">Social</div>
                        <div className="text-gray-400 pt-3 hover:text-gray-300 hover:underline">
                            <a href="https://www.instagram.com/w3b__jmi/" target="_blank" rel="noopener noreferrer">Instagram</a>
                        </div>
                        <div className="text-gray-400 pt-3 hover:text-gray-300 hover:underline">
                            <a href="https://linkedin.com/company/the-w3b-ai-and-blockchain-society-jmi" target="_blank" rel="noopener noreferrer">LinkedIn</a>
                        </div>
                    </div>
                </div>
            </div>
            <div className="flex flex-wrap justify-between items-center border-t-2 border-zinc-800 h-auto px-6 md:px-16 py-5">
                <div className="text-gray-400 text-sm font-semibold">&copy; 2024 W3B</div>
                <div className="flex flex-wrap justify-center md:justify-end text-gray-400 font-semibold mt-2 md:mt-0">
                    <div 
                        className="pr-6 hover:text-gray-300 cursor-pointer"
                        onClick={() => navigate('/')}
                    >
                        Terms of Service
                    </div>
                    <div 
                        className="hover:text-gray-300 cursor-pointer"
                        onClick={() => navigate('/')}
                    >
                        Privacy Policy
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Footer;
